mapper.update:
UPDATE storeInfo
SET store_pause = #{pause}
WHERE storeInfo_key = #{storeInfoKey}

mapper.select:
SELECT store_pause
FROM storeInfo
WHERE storeInfo_key = #{storeInfoKey}

SELECT *
FROM storeInfo
WHERE client_key = #{clientKey}
ORDER BY storeInfo_key DESC
    LIMIT #{endNo} OFFSET #{startNo}

SELECT *
FROM storeInfo
WHERE client_key = #{clientKey}
  AND store_name LIKE CONCAT('%', #{keyword}, '%')
ORDER BY storeInfo_key DESC
    LIMIT #{endNo} OFFSET #{startNo}

SELECT COUNT(*)
FROM storeInfo
WHERE client_key = #{clientKey}

SELECT *
FROM storeInfo
WHERE client_key = #{clientKey}
  AND store_access = #{accessStatus}
ORDER BY storeInfo_key DESC
    LIMIT #{countPerPage} OFFSET #{startNo}

SELECT COUNT(*)
FROM storeInfo
WHERE client_key = #{clientKey}
  AND store_access = #{accessStatus}
